import React, { useState } from "react";
import GeneralSectionRender from "./GeneralSectionRender";
import uniqid from "uniqid";

function GeneralSection() {
  let i = uniqid();
  let [arr, setArr] = useState([]);
  let [edu, setEdu] = useState([]);

  let [person, setPerson] = useState({ Name: "", Email: "", Number: "" });
  let [people, setPeople] = useState([]);
  let [eduProps, setEduProps] = useState({});

  function EduForm() {
    setEduProps({ ...eduProps, [i]: { SchoolName: "", TitleEdu: "" } });

    setArr([...arr, i]);
    console.log(arr);
    console.log(edu);
  }

  function handleChange(e) {
    let name = e.target.name;
    let value = e.target.value;
    console.log(name, value);
    setPerson({ ...person, [name]: value });
  }

  function submitChanges(e) {
    e.preventDefault();
    let newObj = { ...person, id: uniqid() };
    setPeople([newObj]);
  }

  function changeEdu(e) {
    let idOfValue = e.target.parentNode.parentNode.id;
    let name = e.target.name;
    let value = e.target.value;
    setEduProps({
      ...eduProps,
      [idOfValue]: { ...eduProps[idOfValue], [name]: value },
    });
    const ObjToArr = Object.entries(eduProps);
    setEdu([ObjToArr]);
    console.log(edu);
  }

  return (
    <div>
      <div className="GeneralSection">
        <h1 className="FormHeads">General Details</h1>
        <form action="">
          <label htmlFor="name">Name</label>
          <br />
          <input
            type="text"
            name="Name"
            value={person.Name}
            onChange={handleChange}
            id="name"
          />

          <label htmlFor="email">Email</label>
          <br />
          <input
            type="email"
            name="Email"
            value={person.Email}
            onChange={handleChange}
            id="email"
          />
          <label htmlFor="number">Phone-number</label>
          <br />
          <input
            type="text"
            name="Number"
            value={person.Number}
            onChange={handleChange}
            id="number"
          />

          <button className="genSubBtn" onClick={submitChanges} type="submit">
            Submit
          </button>
        </form>
        <h1 className="FormHeads">Education Qualifications</h1>

        {arr.map(function (e) {
          return (
            <div id={e} key={e}>
              <form action="">
                <label htmlFor="SchoolName">School Name</label>
                <br />
                <input
                  type="text"
                  name="SchoolName"
                  value={e.SchoolName}
                  onChange={changeEdu}
                  id="SchoolName"
                />
                <label htmlFor="TitlStudy">Title Of Study</label>
                <br />
                <input
                  type="text"
                  value={e.TitleEdu}
                  onChange={changeEdu}
                  name="TitleEdu"
                  id="TitleStudy"
                />
              </form>
            </div>
          );
        })}

        <button className="EduAddBtn" onClick={EduForm} type="submit">
          Add Education Qualifications
        </button>
      </div>

      <div className="DisplayedValues">
        <GeneralSectionRender details={people} />
        {}
      </div>
    </div>
  );
}

export default GeneralSection;
